import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import Logo from '../Components/Auction_AId_logo_wide_beta.png'

const Landing = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    leagueId: '',
    swid: '',
    espnS2: ''
  });

  const [color, changeColor] = useState("#282c34");

  document.body.style.backgroundColor = "#282c34";

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('/api/league/', formData);
      navigate('/home', { state: { apiResult: response.data, formData } });
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };



  return (

    <div className="flex items-center justify-center min-h-screen bg-gray-100">
      <div className="p-8 bg-[#312f30] rounded shadow-md w-96">
        <div className="mb-4">
          <img src={Logo} alt="App Logo" className="w-full h-auto" />
        </div>
        <p className="block text-white mb-4">Enter your ESPN League details below:</p>
        <form onSubmit={handleSubmit}>
        <label htmlFor="leagueId" className="block text-white mb-1">League ID:</label>
          <input
            type="text"
            name="leagueId"
            value={formData.leagueId}
            onChange={handleInputChange}
            className="w-full p-2 mb-4 border border-gray-600 rounded bg-gray-800 text-white"
          />
          <label htmlFor="swid" className="block text-white mb-1">SWID:</label>
          <input
            type="text"
            name="swid"
            value={formData.swid}
            onChange={handleInputChange}
            className="w-full p-2 mb-4 border border-gray-600 rounded bg-gray-800 text-white"
          />
          <label htmlFor="espnS2" className="block text-white mb-1">ESPN S2:</label>
          <input
            type="text"
            name="espnS2"
            value={formData.espnS2}
            onChange={handleInputChange}
            className="w-full p-2 mb-4 border border-gray-600 rounded bg-gray-800 text-white"
          />
          <button type="submit" className="w-full p-2 bg-blue-600 text-white rounded hover:bg-blue-700">
            Submit
          </button>
        </form>  
      </div>
    </div>
  );
};

export default Landing;
