// App.js and Landing.js remain the same as in the previous version

// Home.js
import React, { useState } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';

const InfoBox = ({ title, value }) => (
  <div className="bg-white p-4 rounded-lg shadow-md flex-1 m-2">
    <h3 className="text-lg font-semibold mb-2 text-gray-700">{title}</h3>
    <p className="text-2xl font-bold text-blue-600">{value}</p>
  </div>
);

const Dropdown = ({ label, options, value, onChange }) => (
  <div className="mb-4">
    <label className="block text-sm font-medium text-gray-700 mb-1">{label}</label>
    <select
      value={value}
      onChange={onChange}
      className="mt-1 block w-full pl-3 pr-10 py-2 text-base border-gray-300 focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm rounded-md"
    >
      {options.map((option) => (
        <option key={option} value={option}>{option}</option>
      ))}
    </select>
  </div>
);

const AuctionTable = ({ auctionData }) => (
    <div className="mt-8 overflow-x-auto">
      <table className="min-w-full divide-y divide-gray-200">
        <thead className="bg-gray-50">
          <tr>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Player Name</th>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Auction Value</th>
          </tr>
        </thead>
        <tbody className="bg-white divide-y divide-gray-200">
          {auctionData.map((player, index) => (
            <tr key={index}>
              <td className="px-6 py-4 whitespace-nowrap">{player.name}</td>
              <td className="px-6 py-4 whitespace-nowrap">${player.value.toFixed(2)}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

const Home = () => {
  const location = useLocation();
  const apiResult = location.state?.apiResult;
  const { leagueId, swid, espnS2 } = location.state?.formData || {};
  const [auctionData, setAuctionData] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  // Parse the JSON string if it's a string, otherwise use it as is
  const leagueData = typeof apiResult === 'string' ? JSON.parse(apiResult) : apiResult;

  const [formState, setFormState] = useState({
    scoringFormat: 'Standard',
    auctionDollarsPerTeam: 200,
    keepers: 'No',
    projectionsSource: 'FantasyPros',
    baselinePlayer: 'First $1 Player',
    vorpCalculation: 'PPG',
    draftStrategy: 'Balanced',
    seasonsToInclude: [...leagueData.previous_seasons]
  });

  const handleDropdownChange = (e) => {
    setFormState({ ...formState, [e.target.name]: e.target.value });
  };

  const handleCheckboxChange = (season) => {
    setFormState(prevState => ({
      ...prevState,
      seasonsToInclude: prevState.seasonsToInclude.includes(season)
        ? prevState.seasonsToInclude.filter(s => s !== season)
        : [...prevState.seasonsToInclude, season]
    }));
  };

  const handleGenerateAuctionAid = async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await axios.post('/api/generate-auction-aid', {
        leagueId,
        swid,
        espnS2,
        ...formState
      });
      setAuctionData(response.data);
    } catch (err) {
      setError('Failed to generate Auction Aid. Please try again.');
      console.error('Error generating Auction Aid:', err);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto">
        <h1 className="text-3xl font-bold text-center mb-8 text-gray-800">League Information</h1>
        <div className="flex flex-wrap justify-center mb-8">
          <InfoBox title="League ID" value={leagueData.league_id} />
          <InfoBox title="League Name" value={leagueData.league_name} />
          <InfoBox title="Season Year" value={leagueData.season_year} />
          <InfoBox title="League Size" value={leagueData.league_size} />
        </div>

        <div className="bg-white shadow-md rounded-lg p-6 mb-8">
          <h2 className="text-2xl font-bold mb-4 text-gray-800">Configuration</h2>
          <Dropdown
            label="Scoring Format"
            options={['Standard', 'PPR', 'Half-PPR']}
            value={formState.scoringFormat}
            onChange={handleDropdownChange}
            name="scoringFormat"
          />
          <Dropdown
            label="Auction $ Per Team"
            options={['200']}
            value={formState.auctionDollarsPerTeam}
            onChange={handleDropdownChange}
            name="auctionDollarsPerTeam"
          />
          <Dropdown
            label="Keepers"
            options={['No']}
            value={formState.keepers}
            onChange={handleDropdownChange}
            name="keepers"
          />
        <Dropdown
            label="Projections Source"
            options={['FantasyPros']}
            value={formState.projectionsSource}
            onChange={handleDropdownChange}
            name="projectionsSource"
          />
          <Dropdown
            label="Baseline Player"
            options={['First $1 Player']}
            value={formState.baselinePlayer}
            onChange={handleDropdownChange}
            name="baselinePlayer"
          />
          <Dropdown
            label="VORP Calculation"
            options={['PPG']}
            value={formState.vorpCalculation}
            onChange={handleDropdownChange}
            name="vorpCalculation"
          />
          <Dropdown
            label="Draft Strategy"
            options={['Balanced']}
            value={formState.draftStrategy}
            onChange={handleDropdownChange}
            name="draftStrategy"
          />

          <div className="mt-4">
            <label className="block text-sm font-medium text-gray-700 mb-2">Seasons to Include</label>
            <div className="space-y-2">
              {leagueData.previous_seasons.map((season) => (
                <label key={season} className="inline-flex items-center mr-4">
                  <input
                    type="checkbox"
                    checked={formState.seasonsToInclude.includes(season)}
                    onChange={() => handleCheckboxChange(season)}
                    className="form-checkbox h-5 w-5 text-indigo-600"
                  />
                  <span className="ml-2 text-gray-700">{season}</span>
                </label>
              ))}
            </div>
          </div>
        </div>
        <div className="mt-8 flex justify-center">
          <button
            onClick={handleGenerateAuctionAid}
            disabled={isLoading}
            className="px-6 py-3 bg-blue-600 text-white font-semibold rounded-md shadow-sm hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 disabled:opacity-50"
          >
            {isLoading ? 'Generating...' : 'Generate Auction Aid'}
          </button>
        </div>

        {error && (
          <div className="mt-4 text-center text-red-600">
            {error}
          </div>
        )}

        {auctionData && <AuctionTable auctionData={auctionData} />}
      </div>
    </div>
  );
};



export default Home;
