// Home.js
import React, { useState } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';
import { FaInfoCircle } from 'react-icons/fa';
import Logo from '../Components/Auction_AId_logo_wide_beta.png'

const InfoBox = ({ title, value }) => (
  <div className="bg-[#272526] p-4 rounded-lg shadow-md flex-1 m-2">
    <h3 className="text-lg font-semibold mb-2 text-gray-300">{title}</h3>
    <p className="text-2xl font-bold text-[#8c52ff]">{value}</p>
  </div>
);

const Dropdown = ({ label, options, value, onChange, name, info }) => (
  <div className="mb-4">
    <div className="flex items-center mb-1">
      <label className="text-sm font-medium text-white">
        {label} 
      </label>
      <div className="relative group">
        <FaInfoCircle className="text-gray-400 cursor-pointer ml-2" />
        <div className="absolute left-1/2 -translate-x-1/2 bottom-full mb-2 hidden group-hover:block bg-black text-white text-xs px-4 py-2 w-56 max-w-xs rounded-md shadow-lg">
          {info}
        </div>
        </div>
    </div>
    <select
      name={name}
      value={value}
      onChange={onChange}
      className="mt-1 block w-full pl-3 pr-10 py-2 text-base border border-[#7042cc] focus:outline-none focus:ring-[#8c52ff] focus:border-[#8c52ff] bg-[#241c1d] text-white sm:text-sm rounded-md"
    >
      {options.map((option) => (
        <option key={option} value={option}>{option}</option>
      ))}
    </select>
  </div>
);

const Accordion = ({ isOpen, toggle, children }) => (
  <div className="border border-[#7042cc] rounded-lg shadow-md mb-4">
    <button
      onClick={toggle}
      className="w-full px-4 py-2 text-left font-semibold text-white bg-[#312f30] rounded-t-lg focus:outline-none"
    >
      {isOpen ? 'Hide Settings ▲' : 'Show Settings ▼'}
    </button>
    {isOpen && <div className="p-4 bg-[#312f30]">{children}</div>}
  </div>
);

const AuctionTable = ({ auctionData }) => (
  <div className="mt-8 overflow-x-auto">
    <table className="min-w-full divide-y divide-[#312f30]">
      <thead className="bg-[#7042cc]">
        <tr>
          <th className="px-6 py-3 text-left text-xs font-medium text-white uppercase tracking-wider">Player Name</th>
          <th className="px-6 py-3 text-left text-xs font-medium text-white uppercase tracking-wider">Auction Value</th>
        </tr>
      </thead>
      <tbody className="bg-[#312f30] divide-y divide-[#7042cc]">
        {auctionData.map((player, index) => (
          <tr key={index}>
          <td className="px-6 py-4 whitespace-nowrap">{player.ecr_avg}</td>
          <td className="px-6 py-4 whitespace-nowrap">{player.player_name}</td>
          <td className="px-6 py-4 whitespace-nowrap">{player.team}</td>
          <td className="px-6 py-4 whitespace-nowrap">{player.pos}</td>
          <td className="px-6 py-4 whitespace-nowrap">{player.curr_year_projected_pos_rank}</td>
          <td className="px-6 py-4 whitespace-nowrap">{player.vorp.toFixed(2)}</td>
          <td className="px-6 py-4 whitespace-nowrap">{player.prev_year_bid_amt.toFixed(0)}</td>
          <td className="px-6 py-4 whitespace-nowrap">{player.projected_pos_rank_avg_bid_amt.toFixed(0)}</td>
          <td className="px-6 py-4 whitespace-nowrap">{player.predicted_bid_amt.toFixed(0)}</td>
          <td className="px-6 py-4 whitespace-nowrap">{player.curr_year_projected_pos_rank.toFixed(0)}</td>
          <td className="px-6 py-4 whitespace-nowrap">{player.recommended_auction_value.toFixed(0)}</td>
