// Home.js
import React, { useState } from 'react';
import axios from 'axios';
import { useLocation } from 'react-router-dom';
import { FaInfoCircle } from 'react-icons/fa';

const InfoBox = ({ title, value }) => (
  <div className="bg-[#312f30] p-4 rounded-lg shadow-md flex-1 m-2">
    <h3 className="text-lg font-semibold mb-2 text-gray-300">{title}</h3>
    <p className="text-2xl font-bold text-[#8c52ff]">{value}</p>
  </div>
);

const Dropdown = ({ label, options, value, onChange, name, info }) => (
  <div className="mb-4">
    <label className="block text-sm font-medium text-white mb-1">
      {label}
      <span className="ml-2 text-gray-400 cursor-pointer">
        <FaInfoCircle title={info} />
      </span>
    </label>
    <select
      name={name}
      value={value}
      onChange={onChange}
      className="mt-1 block w-full pl-3 pr-10 py-2 text-base border border-[#7042cc] focus:outline-none focus:ring-[#8c52ff] focus:border-[#8c52ff] bg-[#241c1d] text-white sm:text-sm rounded-md"
    >
      {options.map((option) => (
        <option key={option} value={option}>{option}</option>
      ))}
    </select>
  </div>
);

const Accordion = ({ isOpen, toggle, children }) => (
  <div className="border border-[#7042cc] rounded-lg shadow-md mb-4">
    <button
      onClick={toggle}
      className="w-full px-4 py-2 text-left font-semibold text-white bg-[#312f30] rounded-t-lg focus:outline-none"
    >
      {isOpen ? 'Hide Settings ▲' : 'Show Settings ▼'}
    </button>
    {isOpen && <div className="p-4 bg-[#241c1d]">{children}</div>}
  </div>
);

const AuctionTable = ({ auctionData }) => (
  <div className="mt-8 overflow-x-auto">
    <table className="min-w-full divide-y divide-[#312f30]">
      <thead className="bg-[#7042cc]">
        <tr>
          <th className="px-6 py-3 text-left text-xs font-medium text-white uppercase tracking-wider">Player Name</th>
          <th className="px-6 py-3 text-left text-xs font-medium text-white uppercase tracking-wider">Auction Value</th>
        </tr>
      </thead>
      <tbody className="bg-[#312f30] divide-y divide-[#7042cc]">
        {auctionData.map((player, index) => (
          <tr key={index}>
          <td className="px-6 py-4 whitespace-nowrap">{player.ecr_avg}</td>
          <td className="px-6 py-4 whitespace-nowrap">{player.player_name}</td>
          <td className="px-6 py-4 whitespace-nowrap">{player.team}</td>
          <td className="px-6 py-4 whitespace-nowrap">{player.pos}</td>
          <td className="px-6 py-4 whitespace-nowrap">{player.curr_year_projected_pos_rank}</td>
          <td className="px-6 py-4 whitespace-nowrap">{player.vorp.toFixed(2)}</td>
          <td className="px-6 py-4 whitespace-nowrap">{player.prev_year_bid_amt.toFixed(0)}</td>
          <td className="px-6 py-4 whitespace-nowrap">{player.projected_pos_rank_avg_bid_amt.toFixed(0)}</td>
          <td className="px-6 py-4 whitespace-nowrap">{player.predicted_bid_amt.toFixed(0)}</td>
          <td className="px-6 py-4 whitespace-nowrap">{player.curr_year_projected_pos_rank.toFixed(0)}</td>
          <td className="px-6 py-4 whitespace-nowrap">{player.recommended_auction_value.toFixed(0)}</td>
        </tr>
        ))}
      </tbody>
    </table>
  </div>
);

const Home = () => {
  const location = useLocation();
  const apiResult = location.state?.apiResult;
  const { leagueId, swid, espnS2 } = location.state?.formData || {};
  const [accordionOpen, setAccordionOpen] = useState(false);
  const [auctionData, setAuctionData] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);

  const leagueData = typeof apiResult === 'string' ? JSON.parse(apiResult) : apiResult;

  const [formState, setFormState] = useState({
    scoringFormat: 'Standard',
    auctionDollarsPerTeam: '200',
    keepers: 'No',
    projectionsSource: 'FantasyPros',
    baselinePlayer: 'First $1 Player',
    vorpCalculation: 'PPG',
    seasonsToInclude: [...leagueData.previous_seasons],
  });

  const handleDropdownChange = (e) => {
    setFormState({ ...formState, [e.target.name]: e.target.value });
  };

  const handleCheckboxChange = (season) => {
    setFormState(prevState => ({
      ...prevState,
      seasonsToInclude: prevState.seasonsToInclude.includes(season)
        ? prevState.seasonsToInclude.filter(s => s !== season)
        : [...prevState.seasonsToInclude, season]
    }));
  };

  const handleGenerateAuctionAid = async () => {
    setIsLoading(true);
    setError(null);
    try {
      const response = await axios.post('/api/generate-auction-aid', {
        leagueId,
        swid,
        espnS2,
        ...formState,
      });
      setAuctionData(response.data);
    } catch (err) {
      setError('Failed to generate Auction Aid. Please try again.');
      console.error('Error generating Auction Aid:', err);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="bg-[#241c1d] min-h-screen flex items-center justify-center">
      <div className="flex items-center justify-center bg-gray-100">
      <div className="max-w-7xl w-full px-6 py-12">
        <h1 className="text-3xl font-bold text-center mb-8 text-[#8c52ff]">League Information</h1>
        <div className="flex flex-wrap justify-center mb-8">
          <InfoBox title="League ID" value={leagueData.league_id} />
          <InfoBox title="League Name" value={leagueData.league_name} />
          <InfoBox title="Season Year" value={leagueData.season_year} />
          <InfoBox title="League Size" value={leagueData.league_size} />
        </div>

        <Accordion isOpen={accordionOpen} toggle={() => setAccordionOpen(!accordionOpen)}>
          <h2 className="text-xl font-bold mb-4 text-white">League Settings</h2>
          <Dropdown
            label="Scoring Format"
            options={['Standard', 'PPR', 'Half-PPR']}
            value={formState.scoringFormat}
            onChange={handleDropdownChange}
            name="scoringFormat"
            info="Select the scoring format used in your league."
          />
          <Dropdown
            label="Auction $ Per Team"
            options={['200']}
            value={formState.auctionDollarsPerTeam}
            onChange={handleDropdownChange}
            name="auctionDollarsPerTeam"
            info="Set the total auction budget per team."
          />
          <Dropdown
            label="Keepers"
            options={['No']}
            value={formState.keepers}
            onChange={handleDropdownChange}
            name="keepers"
            info="Indicate if your league has keepers."
          />
          <div className="mt-4">
            <label className="block text-sm font-medium text-gray-700 mb-2">Seasons to Include</label>
            <div className="space-y-2">
              {leagueData.previous_seasons.map((season) => (
                <label key={season} className="inline-flex items-center mr-4">
                  <input
                    type="checkbox"
                    checked={formState.seasonsToInclude.includes(season)}
                    onChange={() => handleCheckboxChange(season)}
                    className="form-checkbox h-5 w-5 text-indigo-600"
                  />
                  <span className="ml-2 text-gray-700">{season}</span>
                </label>
              ))}
            </div>
          </div>

          <h2 className="text-xl font-bold mt-8 mb-4">Auction AId Settings</h2>
          <Dropdown
            label="Projections Source"
            options={['FantasyPros']}
            value={formState.projectionsSource}
            onChange={handleDropdownChange}
            name="projectionsSource"
            info="Choose the source for player projections."
          />
          <Dropdown
            label="Baseline Player"
            options={['First $1 Player']}
            value={formState.baselinePlayer}
            onChange={handleDropdownChange}
            name="baselinePlayer"
            info="Select the baseline player for value calculations."
          />
          <Dropdown
            label="VORP Calculation"
            options={['PPG']}
            value={formState.vorpCalculation}
            onChange={handleDropdownChange}
            name="vorpCalculation"
            info="Choose the method for VORP calculation."
          />
        </Accordion>

        <div className="mt-8 flex justify-center">
          <button
            onClick={handleGenerateAuctionAid}
            disabled={isLoading}
            className="px-6 py-3 bg-[#7042cc] text-white font-semibold rounded-md shadow-sm hover:bg-[#8c52ff] focus:ring-2 focus:ring-offset-2 focus:ring-[#8c52ff] disabled:opacity-50"
          >
            {isLoading ? 'Generating...' : 'Generate Auction Aid'}
          </button>
        </div>

        {error && <div className="mt-4 text-center text-red-600">{error}</div>}

        {auctionData && <AuctionTable auctionData={auctionData} />}
      </div>
      </div>
    </div>
  );
};

export default Home;
